import './App.css';
import {useState} from "react";
import Axios from 'axios';
import { 
  Input, 
  Button
} from 'semantic-ui-react'

function App() {
  const [BossName, setBossName] = useState("");
  const [EmployeeName, setEmployeeName] =useState("");
  const [BossInput, setBossInput] = useState("");
  const [EmployeeInput, setEmployeeInput] = useState("");

  const addBossTask = ()=>{
    Axios.post('http://localhost:3001/addBossTask', {
      BossName: BossName,
      BossInput: BossInput,
    })
  }

  const addEmployeeTask = ()=>{
    Axios.post('http://localhost:3001/addEmployeeTask', {
      BossName: BossName,
      BossInput: BossInput,
    })
  }


  return (
    <div className="App">
      <div className="AssigningTask">
        <Input fluid icon='' placeholder='Assign Task...' onChange={(event)=> {setBossInput(event.target.value)}}/>
        <Button content='Submit' primary />
        <Button content='Show All' secondary />
      </div>

      <hr/>

      <div className="responseTask">
        <Input fluid icon='' placeholder='What tasks did you complete today?' onChange={ (event)=> {setBossInput(event.target.value) }}/>
        <Button content='Submit' primary />
        <Button content='Show All' secondary />
      </div>

    </div>
  );
}

export default App;
